import './App.css'


function App() {
  const [users, setUsers] = useState();

  const addUserHandler = enteredText => {
    setUsers(prevUsers => {
      const updatedUsers = [...prevUsers];
      updatedUsers.unshift({ userName: enteredText.name, userAge: enteredText.age, id: Math.random().toString() });
      return updatedUsers;
    });
  };

  return (
    <div>
      <section id="user-form">
        <UserInput onAddUser={addUserHandler} />
